"use client"

import * as React from "react"
import { ComponentPropsWithoutRef } from "react"

import { cn } from "@/lib/utils"

interface AvatarProps extends React.HTMLAttributes<HTMLDivElement> {}

function getInitials(name: string | undefined | null): string {
  if (!name || typeof name !== "string") return "??"

  const cleanedName = name.trim()
  if (cleanedName.length === 0) return "??"

  const nameParts = cleanedName.split(/\s+/).filter((part) => part.length > 0)

  if (nameParts.length === 0) return "??"

  if (nameParts.length === 1) {
    return cleanedName.slice(0, 2).toUpperCase()
  }

  return (nameParts[0][0] + nameParts[nameParts.length - 1][0]).toUpperCase()
}

interface AvatarProps {
  className?: string
  imageProps?: ComponentPropsWithoutRef<"img">
  fallbackProps?: ComponentPropsWithoutRef<"div">
  alt?: string
  src?: string
}

export default function Avatar({
  className,
  imageProps,
  fallbackProps,
  alt,
  src,
  ...props
}: AvatarProps & ComponentPropsWithoutRef<"div">) {
  return (
    <AvatarContainer className={className} {...props}>
      <AvatarImage src={src} alt={alt} {...imageProps} />
      <AvatarFallback {...fallbackProps}>{getInitials(alt)}</AvatarFallback>
    </AvatarContainer>
  )
}

function AvatarContainer({ className, ...props }: AvatarProps) {
  return (
    <div
      data-slot="avatar"
      className={cn(
        "relative flex size-8 shrink-0 overflow-hidden rounded-full",
        className
      )}
      {...props}
    />
  )
}

interface AvatarImageProps extends React.ImgHTMLAttributes<HTMLImageElement> {}

function AvatarImage({
  className,
  onLoad,
  onError,
  ...props
}: AvatarImageProps) {
  const [hasError, setHasError] = React.useState(false)
  const { alt } = props

  // Reset error state when src changes
  React.useEffect(() => {
    setHasError(false)
  }, [props.src])

  if (hasError) {
    return null
  }

  return (
    <img
      data-slot="avatar-image"
      className={cn("aspect-square size-full object-cover", className)}
      alt={alt ?? ""}
      onLoad={onLoad}
      onError={(e) => {
        setHasError(true)
        onError?.(e)
      }}
      {...props}
    />
  )
}

interface AvatarFallbackProps extends React.HTMLAttributes<HTMLDivElement> {
  delayMs?: number
}

function AvatarFallback({
  className,
  delayMs = 0,
  children,
  ...props
}: AvatarFallbackProps) {
  const [isVisible, setIsVisible] = React.useState(delayMs === 0)

  React.useEffect(() => {
    if (delayMs > 0) {
      const timer = setTimeout(() => setIsVisible(true), delayMs)
      return () => clearTimeout(timer)
    }
  }, [delayMs])

  if (!isVisible) {
    return null
  }

  return (
    <div
      data-slot="avatar-fallback"
      className={cn(
        "bg-muted flex size-full items-center justify-center rounded-full",
        className
      )}
      {...props}
    >
      {children}
    </div>
  )
}

export { AvatarContainer, AvatarFallback, AvatarImage }

"use client"

import * as React from "react"

import { cn } from "@/lib/utils"

import Button from "./button"

interface TabsContextValue {
  value: string
  onValueChange: (value: string) => void
}

const TabsContext = React.createContext<TabsContextValue | undefined>(undefined)

function useTabs() {
  const context = React.useContext(TabsContext)
  if (!context) {
    throw new Error("Tabs components must be used within a Tabs component")
  }
  return context
}

interface TabsProps {
  value: string
  onValueChange: (value: string) => void
  className?: string
  children: React.ReactNode
  defaultValue?: string
}

export default function Tabs({
  value,
  onValueChange,
  className,
  children,
  defaultValue,
  ...props
}: TabsProps) {
  const [internalValue, setInternalValue] = React.useState(defaultValue || "")

  const isControlled = value !== undefined
  const currentValue = isControlled ? value : internalValue

  const handleValueChange = React.useCallback(
    (newValue: string) => {
      if (!isControlled) {
        setInternalValue(newValue)
      }
      onValueChange?.(newValue)
    },
    [isControlled, onValueChange]
  )

  const contextValue = React.useMemo(
    () => ({
      value: currentValue,
      onValueChange: handleValueChange,
    }),
    [currentValue, handleValueChange]
  )

  return (
    <TabsContext.Provider value={contextValue}>
      <div
        data-slot="tabs"
        className={cn("flex flex-col gap-2", className)}
        {...props}
      >
        {children}
      </div>
    </TabsContext.Provider>
  )
}

interface TabsListProps {
  className?: string
  children: React.ReactNode
}

function TabsList({ className, children, ...props }: TabsListProps) {
  return (
    <div
      data-slot="tabs-list"
      role="tablist"
      className={cn("flex gap-2", className)}
      {...props}
    >
      {children}
    </div>
  )
}

interface TabsTriggerProps {
  value: string
  className?: string
  children: React.ReactNode
  disabled?: boolean
}

function TabsTrigger({
  value,
  className,
  children,
  disabled = false,
  ...props
}: TabsTriggerProps) {
  const { value: selectedValue, onValueChange } = useTabs()
  const isSelected = selectedValue === value

  const handleClick = () => {
    if (!disabled) {
      onValueChange(value)
    }
  }

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Enter" || event.key === " ") {
      event.preventDefault()
      if (!disabled) {
        onValueChange(value)
      }
    }
  }

  return (
    <Button
      data-slot="tabs-trigger"
      role="tab"
      aria-selected={isSelected}
      aria-controls={`tab-content-${value}`}
      tabIndex={isSelected ? 0 : -1}
      disabled={disabled}
      variant={isSelected ? "contained" : "ghost"}
      size="small"
      onClick={() => {
        if (!disabled) onValueChange(value)
      }}
      className={cn(className)}
      {...props}
    >
      {children}
    </Button>
  )
}

interface TabsContentProps {
  value: string
  className?: string
  children: React.ReactNode
}

function TabsContent({
  value,
  className,
  children,
  ...props
}: TabsContentProps) {
  const { value: selectedValue } = useTabs()
  const isSelected = selectedValue === value

  if (!isSelected) {
    return null
  }

  return (
    <div
      data-slot="tabs-content"
      role="tabpanel"
      id={`tab-content-${value}`}
      aria-labelledby={`tab-trigger-${value}`}
      className={cn("flex-1 outline-none", className)}
      {...props}
    >
      {children}
    </div>
  )
}

// Versión con estado interno automático (opcional)
interface SimpleTabsProps {
  defaultValue: string
  className?: string
  children: React.ReactNode
}

function SimpleTabs({
  defaultValue,
  className,
  children,
  ...props
}: SimpleTabsProps) {
  const [value, setValue] = React.useState(defaultValue)

  return (
    <Tabs
      value={value}
      onValueChange={setValue}
      className={className}
      {...props}
    >
      {children}
    </Tabs>
  )
}

export { SimpleTabs, Tabs, TabsContent, TabsList, TabsTrigger }

