import * as React from "react"

import { cn } from "@/lib/utils"

export interface SlotProps extends React.HTMLAttributes<HTMLElement> {
  children: React.ReactElement
}

export const Slot = React.forwardRef<HTMLElement, SlotProps>(
  ({ children, className, ...props }, ref) => {
    if (!React.isValidElement(children)) return null

    return React.cloneElement(children, {
      ...props,
      ref,
      className: cn(className, (children as any).props?.className),
    } as any) as React.ReactElement & { ref: React.Ref<HTMLElement> }
  }
)

Slot.displayName = "Slot"

import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

export function absoluteUrl(path: string) {
  return `${process.env.NEXT_PUBLIC_APP_URL}${path}`
}

export function getLocalizedUrl(url: string, targetLocale: string) {
  return url.replace(/^\/(en|es)(\/|$)/, `/${targetLocale}$2`)
}

"use client"

import React, {
  createContext,
  useContext,
  useLayoutEffect,
  useState,
} from "react"

type Theme = string

interface ThemeContextType {
  theme: Theme
  setTheme: (theme: Theme) => void
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined)

export function AodeSuThemeProvider({
  children,
  defaultTheme = "light",
  availableThemes = ["light", "dark"],
  storageKey = "ui-theme",
  stylePath = "/styles/aodesu",
}: {
  children: React.ReactNode
  defaultTheme?: Theme
  availableThemes?: Theme[]
  storageKey?: string
  stylePath?: string
}) {
  const [theme, setThemeState] = useState<Theme>(() => {
    if (typeof window === "undefined") return defaultTheme

    const stored = localStorage.getItem(storageKey) as Theme | null
    const system = window.matchMedia("(prefers-color-scheme: dark)").matches
      ? "dark"
      : "light"
    return stored || (availableThemes.includes(system) ? system : defaultTheme)
  })

  const applyTheme = (theme: Theme, stylePath: string) => {
    const root = document.documentElement

    // Remove all theme classes
    availableThemes.forEach((t) => {
      root.classList.remove(t)
    })

    // Add the new theme class
    root.classList.add(theme)

    // Import the appropriate theme CSS
    const link = document.getElementById("theme-style") as HTMLLinkElement
    if (link) {
      link.href = `${stylePath}/${theme}.css`
    } else {
      const newLink = document.createElement("link")
      newLink.id = "theme-style"
      newLink.rel = "stylesheet"
      newLink.href = `${stylePath}/${theme}.css`
      document.head.appendChild(newLink)
    }
  }

  useLayoutEffect(() => {
    applyTheme(theme, stylePath)
  }, [theme, stylePath, availableThemes])

  const setTheme = (newTheme: Theme) => {
    if (!availableThemes.includes(newTheme)) {
      console.warn(
        `Theme "${newTheme}" is not in availableThemes: ${availableThemes.join(", ")}`
      )
      return
    }

    setThemeState(newTheme)
    localStorage.setItem(storageKey, newTheme)
    applyTheme(newTheme, stylePath)
  }

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  )
}

export function useAodeSuTheme() {
  const context = useContext(ThemeContext)
  if (context === undefined) {
    throw new Error("useAodeSuTheme must be used within an AodeSuThemeProvider")
  }
  return context
}

